        $counter = 1;
        foreach ($orders as $order) {
            $total = $order['quantity'] * $order['unit_price'];
            $taxAmount = ($total * $order['tax_rate']) / 100;
            $totalTax += $taxAmount;
            $totalDiscount += $order['discount'];
            $subtTotalAmount += $total;
            $totalAmount += ($total + $taxAmount - $order['discount']);
        ?>
